import { Component, OnInit } from '@angular/core';
import { IssueService } from '../issue.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Issue } from '../issues';

@Component({
  selector: 'app-issue',
  templateUrl: 'issue-list.component.html'
})
export class IssuesListListComponent implements OnInit {

  issues$ = this.issueService.find();

  issueToEdit?: Issue;

  issueForm = new FormGroup({
    title: new FormControl('', [Validators.required]), 
    description: new FormControl('', [Validators.required]), 
  });

  constructor(private issueService: IssueService) {
  }

  ngOnInit(): void {

  }

  onSubmit(): void {
    console.log('[submit]');
    console.log();
    if (this.issueForm.valid) {
      this.issueService.save(this.issueForm.value as Issue).subscribe(
        res => {
          this.issues$ = this.issueService.find();
        }
      );
    }
  }

  onDelete(id: string): void {
    this.issueService.delete(id).subscribe(
      res => {
        this.issues$ = this.issueService.find();
      }
    );
  }

  onEdit(issue: Issue): void {
    this.issueToEdit = issue; 
  }

}
<nav aria-label="breadcrumb">
  <ol class="breadcrumb">
    <li class="breadcrumb-item"><a routerLink="/">Home</a></li>
    <li class="breadcrumb-item active">Issue</li>
  </ol>
</nav>
<div class="card">
  <div class="header">
    <h2 class="title">Issue List</h2>
  </div>
</div>

<div class="card">
  <div class="header" >
    <form [formGroup]="issueForm" (ngSubmit)="onSubmit()">
      Title:
      <input type="text" formControlName="title"/>
      <small *ngIf="issueForm.get('title')?.errors?.['required']">Email is required.</small>

      Description:
      <input type="text" formControlName="description"/>
      <small *ngIf="issueForm.get('description')?.errors?.['required']">Email is required.</small>
      <br/>

      <button type="submit">Submit</button>
    </form>
  </div>
</div>

<div class="card">
  <div class="table-responsive">
    <table class="table table-centered table-hover mt-2" id="datatable">
      <thead>
        <tr>
          <th class="border-top-0" scope="col">Id</th>
          <th class="border-top-0" scope="col">Title</th>
          <th class="border-top-0" scope="col">Description</th>
          <td class="border-top-0" scope="col">Actions</td>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of issues$ | async">

          <td>{{item._id}}</td>
          <td>{{item.title}}</td>
          <td>{{item.description}}</td>
          <td>
            <button (click)="onEdit(item)">Edit</button> 
            &nbsp;&nbsp;
            <button (click)="onDelete(item._id)">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
